import React from 'react';

import {connect} from "react-redux";
import  {ListOfUsers} from "./conversations/RecieverList";
import TeamMemberInfo  from './conversations/TeamMemberInfo';
import SearchContact from './conversations/SearchContact';
import AddContact from "./conversations/AddContact";
import SenderConversations from "./conversations/SenderConversations";
import {NotificationContainer, NotificationManager} from 'react-notifications';
import 'react-notifications/lib/notifications.css';
import {conversation} from "../_actions";
import axis from "axis.js";
const mapStateToProps = state => ({
    receivers: state.conversation
});

class ConversationCmp extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            senders: []
        };
        this.searchRef = React.createRef();
    }
    openComposeModal(e){
        e.preventDefault();
        this.props.dispatch(conversation.showComposeModal(false));
    }

    render () {
        return (
            <section className="content conversation">
                <div className="row">
                    <div className={`col-sm-4 conversation-sidebar ${!axis.isUndefined(this.props.receivers.showDetails) && this.props.receivers.showDetails ? 'hide-sidebar' : ''}`}>
                        <div className="box box-solid">
                            <div className="box-header with-border">
                                <TeamMemberInfo />
                            </div>
                            <div className="box-search">
                                <SearchContact searchRef={this.searchRef}/>
                                <AddContact/>
                            </div>
                            <ListOfUsers searchRef={this.searchRef}/>
                        </div>
                        <div className="mobile-compose-btn">
                            <button type="button" onClick={(e) => this.openComposeModal(e)}><i className="fa fa-edit"></i> Compose</button>
                        </div>
                    </div>

                    <div className="col-sm-8">
                        <SenderConversations/>
                    </div>
                </div>
            </section>
        );
    }
}

const Conversation = connect(mapStateToProps)(ConversationCmp);
export { Conversation as Conversation };
