import React from 'react';

class ProfilePicture extends React.Component{
    constructor(props){
        super(props);
    }

    render() {
        const user = this.props.user;
        if(typeof user.picture === 'undefined'){
            return (
                <p className={"no-image"}>{user.first_name.charAt(0)}</p>
            );
        }

        return (
            <img src={user.picture.data.file.replace('.test', '.com')} className={"img-circle"}/>
        );
    }
}

export default ProfilePicture;
