import React from 'react';
import {connect} from "react-redux";
import {allSenders} from "../../_actions/senders.action";

let _queryTimeout = null;
class SearchContact extends React.Component {
    constructor(props){
        super(props);
    }
    handleSearch(e){
        e.preventDefault();
        const searchKeyWord = this.props.searchRef.current.value;
        if (_queryTimeout) {
            window.clearTimeout(_queryTimeout);
        }
        if(!searchKeyWord.length){
            this.props.dispatch(allSenders.loadAllSenders(1));
            return;
        }
        _queryTimeout = window.setTimeout(function(){
            this.props.dispatch(allSenders.searchSenders(searchKeyWord,1));
        }.bind(this,allSenders,searchKeyWord),400);

    }
    render() {
        return(
            <div className="search-box">
                <input type="text" name="search_contact" className="form-control" placeholder="Search your contact" onChange={(e)=>this.handleSearch(e)} ref={this.props.searchRef}/>
                <button type="button" className="search-contact btn btn-info" onClick={(e)=>this.handleSearch(e)}>
                    <i className="fa fa-search"/>
                </button>
            </div>
        );
    }
}

export default connect()(SearchContact);
