    componentDidMount() {
        this.props.loadDashBoard({ title : "bla bla"});
    }

    render() {
        console.log(this.props);
        let fields = this.props.reservations.map(function(data,index) {
            return (
                <div className="col-md-3" key={index}>{data.reservation_date}</div>
            )
        });


        return (
            <div className='App'>

                <Container>
                    <Row>
                        Hi
                        <input
                            type="button"
                            id="title"
                            value="Click me"
                            onClick={this.handleChange}
                        />
                    </Row>
                        {fields}
                    <Row>

                    </Row>
                </Container>
            </div>

        );
    }
}


function mapStateToProps(state) {
    console.log(state);
    const {loading} = state.report;
    const {reservations} = state.report;
    return {
        loading,
        reservations
    };
}

const connectedApp = connect(mapStateToProps, mapDispatchToProps)(App);
export {connectedApp as App};
