import React from 'react';
import {Conversation} from "../Conversation";
import ConversationItemHeader from "./ConversationItemHeader";
import ConversationItemBody from "./ConversationItemBody";

class EachConversation extends React.Component{
    constructor(props){
        super(props);
    }
    render() {
        return (
            <div className={`conversation-item ${this.props.conversation.communication_type == 1 ? 'sent' : ''}`}>
                <ConversationItemHeader  userConversation={this.props.conversation}/>
                <ConversationItemBody    conversation={this.props.conversation}/>
            </div>
        );
    }
}

export default EachConversation;
