import React from 'react';
import {Router, Route, Link, withRouter, Redirect } from 'react-router-dom';

import { history } from '../_helpers';
import {PrivateRoute} from "./../components/PrivateRoute"
import Footer from "./../components/Footer"
import {Dashboard} from "./../components/Dashboard"
import {Conversation} from "./../components/Conversation"
import {Users} from "./../components/Users"
import {Login} from "./../components/Login"
import { connect } from 'react-redux';
import Settings  from './Settings';
import  {userActions} from './../_actions/user.actions'
import {userService} from "../_services";
import {NotificationContainer} from 'react-notifications';
import 'react-notifications/lib/notifications.css';
import Config from "./Config";
import Aupair from "./Aupair";
import Family from "./Family";
import ComposeMessage from "./ComposeMessage";
class App extends React.Component {
    constructor(props) {
        super(props);
        this.logout = this.logout.bind(this);
        this.state={
            showMenu: false
        };
    }

    logout(e){
        userService.logout().then(()=>{
            this.props.dispatch(userActions.logout());
            history.push('/login');
        });
    }
    mobileMenuBtn() {
        return (
            <button className="mobile-menu-btn" onClick={e => this.handleMenu(e)}>
                <i className="fa fa-bars"></i>
            </button>
        );
    }
    handleMenu(e) {
        e.preventDefault();
        if(this.state.showMenu){
            this.setState({
                showMenu: false
            })
        }else{
            this.setState({
                showMenu: true
            })
        }
    }
    hideMenu(){
        this.setState({
            showMenu: false
        })
    }
    conversionMenu() {
        return (
            <li className="dropdown messages-menu">
                <Link to={"/conversation"} onClick={e => this.hideMenu()}>  <i className="fa fa-envelope-o"/>
                    Conversation
                </Link>
            </li>
        )
    }
    settingsMenu() {
        return (
            <li className="dropdown settings-menu">
                <Link to={"/settings"} onClick={e => this.hideMenu()}>  <i className="fa fa-cog"/>
                    Settings
                </Link>
            </li>
        )
    }
    aupairMenu() {
        return (
            <li className="dropdown settings-menu">
                <Link to={"/aupair"} onClick={e => this.hideMenu()}>  <i className="fa fa-user-secret"/>
                    Aupair
                </Link>
            </li>
        )
    }
    familyMenu() {
        return (
            <li className="dropdown settings-menu">
                <Link to={"/family"} onClick={e => this.hideMenu()}>  <i className="fa fa-users"/>
                    Family
                </Link>
            </li>
        )
    }

    loginMenu(loggedIn) {
        return (
            <li className="dropdown messages-menu">
                <Link to="/login" onClick={e => this.hideMenu()}>

                    Login
                </Link>
            </li>
        )
    }

    render() {
        const {loggedIn} = this.props.authentication;
        return (
            <Router history={history}>

                <header className={`main-header ${loggedIn ? 'logged-in' : ''} ${this.state.showMenu ? 'menu-open ':''}`}>
                    <Link className="logo" to="/">
                        <span className="logo-lg">
                            <img src={Config.PROJECT_URL + "/assets/img/logo-header.png"} alt=""/>
                        </span>
                    </Link>
                    {loggedIn && this.mobileMenuBtn()}
                    <nav className="navbar navbar-static-top">
                        <div className="navbar-custom-menu">
                            <ul className="nav navbar-nav">
                                {loggedIn && this.conversionMenu()}
                                {loggedIn && this.aupairMenu()}
                                {loggedIn && this.familyMenu()}
                                {loggedIn && this.settingsMenu()}
                                {!loggedIn && this.loginMenu(loggedIn)}

                                <li className="dropdown messages-menu">
                                    <Link onClick={(e)=>{this.logout(e)}} to="/logout">
                                        {loggedIn && <i className="fa fa-sign-out"/>}
                                        {loggedIn && "Logout"}
                                    </Link>
                                </li>
                            </ul>
                        </div>
                    </nav>
                </header>
                <div className="wraper  conversation-wrapper">
                    <div className="content-wrapper">
                        <Route path="/login" component={Login}/>
                        <PrivateRoute path="/users" component={Users}/>
                        <PrivateRoute exact path="/" component={Dashboard}/>
                        <PrivateRoute path="/conversation" component={Conversation}/>
                        <PrivateRoute path="/aupair" component={Aupair}/>
                        <PrivateRoute path="/family" component={Family}/>
                        <PrivateRoute path="/settings" component={Settings}/>
                        <NotificationContainer/>
                    </div>
                    <Footer/>
                </div>
                <ComposeMessage/>
            </Router>

        );
    }
}

function AupairMenu() {
    return (
        <li className="dropdown settings-menu">
            <Link to={"/aupair"} >  <i className="fa fa-user-secret"/>
                Aupair
            </Link>
        </li>
    )
}
function FamilyMenu() {
    return (
        <li className="dropdown settings-menu">
            <Link to={"/family"} >  <i className="fa fa-users"/>
                Family
            </Link>
        </li>
    )
}

function LoginMenu(loggedIn) {
    return (
        <li className="dropdown messages-menu">
            <Link to="/login">

                Login
            </Link>
        </li>
    )
}

function mapStateToProps(state) {

    const { authentication } = state;
    return {
        authentication
    };
}

const connectedApp = connect(mapStateToProps)(App);
export { connectedApp as App };
