import React from 'react';
import Config from "../Config";


const File = ({path, type}) => {
    let imagePath = '';
    switch(type){
        case 'jpeg':
            imagePath = path;
            break;
        case 'jpg':
            imagePath = path;
            break;
        case 'png':
            imagePath = path;
            break;
        case 'pdf':
            imagePath = Config.PROJECT_URL + '/assets/img/pdf.png';
            break;
        case 'doc':
            imagePath = Config.PROJECT_URL + '/assets/img/doc.png';
            break;
        case 'docx':
            imagePath = Config.PROJECT_URL + '/assets/img/doc.png';
            break;
        default:
            imagePath = Config.PROJECT_URL + '/assets/img/file.png';
    }
    return (
        <a href={path} download='Attachemnt.jpg' target={"_blank"}>
            <img src={imagePath} className="img-rounded"/>
        </a>
    )
}
class ConversationItemAttachment extends React.Component{
    constructor(props){
        super(props);
    }

    render() {
        let attachments=[];
        if(this.props.attachments.data.length){
            attachments = this.props.attachments.data;
        }
        return (
            <div className="conversation-item-attachments">
                <h5>Attachments</h5>
                <ul>
                    {attachments.map((attachment, index) => {
                        return <li key={'key-' + index}>
                            <File type={attachment.file_type} path={attachment.file_path}/>
                        </li>
                    })}
                </ul>
            </div>
        );
    }
}
export default ConversationItemAttachment;
