import { reportConstants } from '../_constants';
import { reportService } from '../_services';

export const report = {
    loadDashboard
};

const getReportSuccess = reports => ({
    type: reportConstants.REPORT_SUCCESS,
    payload: reports,
});

function loadDashboard() {
    return dispatch => {

        fetch("http://localhost:8000/api/booking/list")
            .then(response => response.json())
            .then(data => {
                dispatch(getReportSuccess(data.payload));
            });


        // dispatch(getReportLoading(true));
        // reportService.loadDashboard()
