import React from 'react';
import { connect } from 'react-redux';
import { userActions } from '../_actions';




const EachError = (props) => {
    return (
        <div className={"form-group has-error"}>
            <div className="help-block"> {props.message[0]}</div>
        </div>
    )
}
class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: '',
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { email, password } = this.state;
        const { dispatch } = this.props;
        if (email && password) {
            dispatch(userActions.login(email, password));
        }
    }

    render() {
        let errorsMessage = [];
        if(this.props.validationErrorMsg){
            errorsMessage = this.props.validationErrorMsg;
        }
        const { loggingIn } = this.props;
        const { email, password, submitted } = this.state;
        return (
            <div className="row">
                <div className="col-md-6 col-md-offset-3">
                    <div className="login-panel">
                        <div className="login-panel-header">
                            <img src={"https://nihaoaupair.com/assets/img/logo-header.png"} alt=""/>
                            <h2 className="title">Login to your account</h2>
                        </div>
                        <form name="form" onSubmit={this.handleSubmit}>
                            {Object.keys(errorsMessage).map((key)=>{
                               return <EachError key={key} message={errorsMessage[key]}/>
                            })}

                            <div className={'form-group' + (submitted && !email ? ' has-error' : '')}>
                                <label htmlFor="email">Email</label>
                                <input type="text" className="form-control" name="email" value={email} onChange={this.handleChange} />
                                {submitted && !email &&
                                <div className="help-block">Email is required</div>
                                }
                            </div>
                            <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                                <label htmlFor="password">Password</label>
                                <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                                {submitted && !password &&
                                <div className="help-block">Password is required</div>
                                }
                            </div>
                            <div className="form-group">
                                <button className="btn btn-primary">Login</button>
                                {loggingIn &&
                                <img src="data:image/gif;base64," />
                                }
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

function mapStateToProps(state) {
    const { loggingIn, validationError , validationErrorMsg} = state.authentication;
    return {
        loggingIn,
        validationError,
        validationErrorMsg
    };
}

const connectedLoginPage = connect(mapStateToProps)(LoginPage);
export { connectedLoginPage as Login };
