import React from 'react';
import {connect} from "react-redux";
import axis from "axis.js";
import {conversation, userActions, contactActions} from "../_actions";
import Pagination from "react-js-pagination";


let _queryTimeout = null;

class Aupair extends React.Component {
    constructor(props) {
        super(props);
        this.searchUserRef = React.createRef();
    }
    componentDidMount() {
        this.props.dispatch(userActions.getAupairs(1,''));
    }
    handlePageChange(pageNumber) {
        const searchKeyWord = this.searchUserRef.current.value;
        this.props.dispatch(userActions.getAupairs(pageNumber, searchKeyWord));
    }
    getPagination(){
        if(!axis.isUndefined(this.props.users.meta.pagination) && this.props.users.meta.pagination.total_pages > 1){
            return <Pagination
                    activePage={this.props.users.meta.pagination.current_page}
                    itemsCountPerPage={this.props.users.meta.pagination.per_page}
                    totalItemsCount={this.props.users.meta.pagination.total}
                    pageRangeDisplayed={5}
                    onChange={this.handlePageChange.bind(this)}
                />;
        }
        return '';
    }

    handleSearch(e){
        e.preventDefault();
        if(axis.isUndefined(this.searchUserRef) ||  !this.searchUserRef.current.value.length){
            this.props.dispatch(userActions.getAupairs(1, ''));
            return;
        }

        const searchKeyWord = this.searchUserRef.current.value;

        if (_queryTimeout) {
            window.clearTimeout(_queryTimeout);
        }

        _queryTimeout = window.setTimeout(function(){
            this.props.dispatch(userActions.getAupairs(1,searchKeyWord));
        }.bind(this,searchKeyWord),400);
    }
    getPicture(path){
        return <img src={path} alt="user pic" className="img-circle"/>;
    }
    getNoImage(firstName){
        return <p className="no-image">{firstName.charAt(0)}</p>;
    }
    handleSendMessage(e, user){
        e.preventDefault();
        this.props.dispatch(conversation.showComposeModal(true,{
            first_name: user.first_name,
            sur_name: user.sur_name,
            user_id: user.user_id
        }));
    }

    addUserToConversation(e,user){
        e.preventDefault();
        this.props.dispatch(contactActions.addContact(user.user_id,1,()=>{
            this.props.dispatch(userActions.getAupairs(this.props.users.meta.pagination.current_page,''));
        }));
    }

    shoMessageButton(authUser, admin, user) {
        if(axis.isUndefined(authUser)){
            return (
                ""
            );
        }

        if(axis.isUndefined(admin)){
            return (
                <button type="button" className="btn btn-sm btn-primary" onClick={e => this.addUserToConversation(e, user)}><i  className="fa fa-plus" aria-hidden="true"/> Add this user </button>
            );
        }

        if(axis.isUndefined(admin) || authUser.id !== admin.data.member_id){
            return (
                <span className={"label label-danger"}>Already Connected</span>
            );
        }

        return (
            <button type="button" className="btn btn-sm btn-info" onClick={e => this.handleSendMessage(e,user)}><i  className="fa fa-paper-plane-o" aria-hidden="true"/> Send Message </button>
        );
    }


    render() {
        let auth = JSON.parse(localStorage.getItem('user'));
        return (
            <section className="content settings">
                <div className="container">
                    <div className="row">
                        <div className="col-xs-12">
                            <div className="box user-data-list">
                                <div className="box-header with-border">
                                    <div className="title">
                                        <h3 className="box-title">Aupair</h3>
                                    </div>
                                    <div className="action">
                                       <div className="form-group">
                                           <input className={"form-control"} type="text" placeholder={"Search aupair..."} onChange={(e)=>this.handleSearch(e)} ref={this.searchUserRef}/>
                                       </div>
                                    </div>
                                </div>
                                <div className={`box-body `}>
                                    <div className={`table-holder table-responsive ${!axis.isUndefined(this.props.users.loading) && this.props.users.loading ? 'is-loading' : ''}`}>
                                        <table className="table table-bordered table-hover">
                                            <thead>
                                            <tr key={"user-columns"}>
                                                <th>Name</th>
                                                <th>Email</th>
                                                <th>Nationality</th>
                                                <th>Registration Date</th>
                                                <th>Last login</th>
                                                <th>Agent</th>
                                                <th>Action</th>
                                            </tr>
                                            </thead>
                                            <tbody>
                                            {this.props.users.items.map((each, index) => {
                                                return <tr key={`user-${index}`}>
                                                    <td>
                                                        <div className="user">
                                                            {!axis.isUndefined(each.picture) ? this.getPicture(each.picture.data.file) : this.getNoImage(each.first_name)}
                                                            <span className="name"> {each.first_name} {each.sur_name}</span>
                                                        </div>
                                                    </td>
                                                    <td>{each.email}</td>
                                                    <td>{!axis.isUndefined(each.logistic)  && !axis.isUndefined(each.logistic.data.nationality) ? each.logistic.data.nationality.data.name : 'N/A'}</td>
                                                    <td>{!axis.isUndefined(each.registration_date) ? each.registration_date : 'N/A'}</td>
                                                    <td>
                                                        <span className={"label bg-green"}>{!axis.isUndefined(each.last_login) ? each.last_login : 'N/A'}</span>
                                                    </td>
                                                    <td>{!axis.isUndefined(each.admin) && !axis.isUndefined(each.admin.data.adminMember.data) ? each.admin.data.adminMember.data.first_name + ' ' + each.admin.data.adminMember.data.last_name : 'N/A'}</td>
                                                    <td>
                                                        {this.shoMessageButton(auth.payload.user, each.admin, each)}
                                                    </td>
                                                </tr>
                                            })}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                                <div className="box-footer clearfix">
                                    {this.getPagination()}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        );
    }


}

const mapStateToProps = state => state;
export default connect(mapStateToProps)(Aupair);
