import {sendersConstants} from "../_constants";
const initialState = {
    data : [],
    page:1,
    search:""
};
export const allSenders = (state = initialState, action) => {
    switch (action.type) {
        case sendersConstants.GET_ALL_SENDERS:
            const sendersPage = action.page;
            if(sendersPage === 1){
                return {
                    ...state,
                    data: [...action.payload.data],
                    meta: {...action.payload.meta},
                    page: action.page
                };
            }
            return {
                ...state,
                data: [...state.data, ...action.payload.data],
                meta: {...action.payload.meta},
                page: action.page
            };
        case sendersConstants.SEARCH_IN_SENDERS:
            const page = action.page;
            if(page === 1){
                return {
                    ...state,
                    data: [...action.payload.data],
                    meta: {...action.payload.meta},
                    page: action.page,
                    search: action.search
                };
            }
            return {
                ...state,
                data: [...state.data, ...action.payload.data],
                meta: {...action.payload.meta},
                page: action.page,
                search: action.search
            };
        case sendersConstants.SEARCH_SENDER_REQUEST:
            return{
                ...state,
                search_loading: true
            };
        case sendersConstants.SEARCH_SENDER_SUCCESS:
            return{
                ...state,
                search_loading: false
            };
        default:
            return state;
    }
};
