import React, {useReducer} from 'react';
import {connect} from 'react-redux';
import 'react-notifications/lib/notifications.css';
import {Button, Card, Container, Row,Col} from "react-bootstrap";
import {report} from "../_actions";
import "../styles.css";
import {DateRangeInput} from "@datepicker-react/styled";
import { ThemeProvider } from "styled-components";


const style = {
    zIndex: "10000000",

};


function mapDispatchToProps(dispatch) {
    return {
        loadDashBoard: article => dispatch(report.loadDashboard(article))
    };
}

const initialState = {
    startDate: null,
    endDate: null,
    focusedInput: null,
}

function reducer(state, action) {
    switch (action.type) {
        case 'focusChange':
            return {...state, focusedInput: action.payload}
        case 'dateChange':
            console.log(action.payload);
            return action.payload
        default:
            throw new Error()
    }
}

function MyCustomDatePicker() {
    const [state, dispatch] = useReducer(reducer, initialState)

    return (

        <ThemeProvider
            theme={{
                breakpoints: ['32em', '48em', '64em'],
                reactDatepicker: {
                    datepickerZIndex : 100000
                },
            }}
        >
        <DateRangeInput
            onDatesChange={data => dispatch({type: 'dateChange', payload: data})}
            onFocusChange={focusedInput => dispatch({type: 'focusChange', payload: focusedInput})}
            startDate={state.startDate} // Date or null
            endDate={state.endDate} // Date or null
            focusedInput={state.focusedInput} // START_DATE, END_DATE or null
        />

        </ThemeProvider>
    )
}


class App extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            showModal: false
        };

    }

    handleChange(event) {

    }

    componentDidMount() {
        //this.props.loadDashBoard();
    }

    render() {

        let cards = this.props.reservations.map((val, key) => {
            return (
                <React.Fragment>
                    <Card style={{ width: '18rem' }} className='col-md-2 m-2'>
                        <Card.Body>
                            <Card.Title>hi {val.reservation_date}</Card.Title>
                            <Card.Text>
                                {val.reservation_date}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </React.Fragment>
            )
        });


        return (
            <div className='App'>

                <Container>
                    <Row>
                        <Col>
                                <MyCustomDatePicker/>
                        </Col>

                        <Col>
                            <>
                                <Button onClick={this.openModal} variant="primary" size="lg" active>
                                    {"+"} Book Room
                                </Button>
                            </>
                        </Col>
                    </Row>

                    <Row>
                        {cards}
                    </Row>
                </Container>
            </div>

        );
    }
}


function mapStateToProps(state) {
    console.log(state);
    const {loading} = state.report;
    const {startDate} = state.report;
    const {endDate} = state.report;
    const {focusedInput} = state.report;
    const {reservations} = state.report;
    return {
        loading,
        reservations,
        startDate,
        endDate,
        focusedInput
    };
}

const connectedApp = connect(mapStateToProps, mapDispatchToProps)(App);
export {connectedApp as App};
