import React from 'react';
import {connect} from "react-redux";
import {conversation} from '../../_actions';
import {Button, Modal, ModalHeader, ModalBody, ModalFooter} from 'reactstrap';
import Form from "react-validation/build/form";
import Input from 'react-validation/build/input';
import Textarea from 'react-validation/build/textarea';
import VButton from 'react-validation/build/button';
import axis from "axis.js";
import axios from 'axios';
import Tooltip from '@atlaskit/tooltip';
import {components} from 'react-select';
import Async from 'react-select/lib/Async';
import Config from "../Config";
import { Editor } from '@tinymce/tinymce-react';
import {authHeader, handleResponse} from '../../_helpers';
import {NotificationManager} from "react-notifications";

class ComposeMessageBtn extends React.Component {
    constructor(props) {
        super(props);
    }

    openModal(e) {
        e.preventDefault();
        this.props.dispatch(conversation.showComposeModal(false,null));
    }
    hideDetails(e){
        e.preventDefault();
        this.props.dispatch(conversation.hideDetails());
    }
    render() {
        return (
            <div className="compose-btn">
                <button type="button" className="btn btn-primary main-btn" onClick={(e) => this.openModal(e)}>
                    <i className="fa fa-edit"/> &nbsp;
                    Compose
                </button>
                <button type="button" className="btn btn-primary back-btn" onClick={(e) => this.hideDetails(e)}>
                    <i className="fa fa-reply"/> &nbsp;
                    Back
                </button>
            </div>

        );
    }
}

export default connect( state => ({
    conversation: state.conversation
}))(ComposeMessageBtn);
