import React from 'react';
import {connect} from 'react-redux';
import 'react-notifications/lib/notifications.css';
import {Button, Card, Container, Row,Col, Form} from "react-bootstrap";
import {report,dateRangeActions } from "../_actions";
import "../styles.css";
import {DateRangeInput} from "@datepicker-react/styled";
import { ThemeProvider } from "styled-components";
import moment from 'moment';


function mapDispatchToProps(dispatch) {
    return {
        loadDashBoard: article => dispatch(report.loadDashboard(article)),
        dateChangeFun: data => dispatch(dateRangeActions.dateChange(data)),
        focusChangeFun: focusInput => dispatch(dateRangeActions.focusChange(focusInput))
    };
}

class App extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

        this.state = {
            firstname: "",
            lastname: "",
            email: "",
            errors: []
        };
    }

    handleInputChange(event) {
        var key = event.target.name;
        var value = event.target.value;
        var obj = {};
        obj[key] = value;
        this.setState(obj);
    }


    handleChange() {
        let startDate = moment(this.props.startDate).format('YYYY-MM-DD');
        let endDate = moment(this.props.endDate).format('YYYY-MM-DD');
        let date = [];

        for (var m = moment(startDate); m.isSameOrBefore(endDate); m.add(1, 'days')) {
            date.push(m.format('YYYY-MM-DD'));
        }
        console.log(date);
    }

    hasError(key) {
        return this.state.errors.indexOf(key) !== -1;
    }
    handleSubmit(event) {
        event.preventDefault();

        //VALIDATE
        var errors = [];

        //firstname
        if (this.state.firstname === "") {
            errors.push("firstname");
        }

        //email
        const expression = /\S+@\S+/;
        var validEmail = expression.test(String(this.state.email).toLowerCase());

        if (!validEmail) {
            errors.push("email");
        }

        this.setState({
            errors: errors
        });

        if (errors.length > 0) {
            return false;
        } else {
            alert("everything good. submit form!");
        }
    }

    componentDidMount() {
        this.props.loadDashBoard();
    }

    render() {
        return (
            <div className='App'>

                <Container fluid="md">

                    <form className="row">
                        <div className="col-lg-6">
                            <label htmlFor="firstname">First Name</label>
                            <input
                                autoComplete="off"
                                className={
                                    this.hasError("firstname")
                                        ? "form-control is-invalid"
                                        : "form-control"
                                }
                                name="firstname"
                                value={this.state.firstname}
                                onChange={this.handleInputChange}
                            />
                            <div
                                className={
                                    this.hasError("firstname") ? "inline-errormsg" : "hidden"
                                }
                            >
                                Please enter a value
                            </div>
                        </div>

                        <div className="col-lg-6">
                            <label htmlFor="email">Email</label>
                            <input
                                autoComplete="off"
                                className={
                                    this.hasError("email")
                                        ? "form-control is-invalid"
                                        : "form-control"
                                }
                                name="email"
                                value={this.state.email}
                                onChange={this.handleInputChange}
                            />
                            <div
                                className={this.hasError("email") ? "inline-errormsg" : "hidden"}
                            >
                                Email is invalid or missing
                            </div>
                        </div>

                        <div className="col-lg-12  padd-top">
                            <button className="btn btn-success" onClick={this.handleSubmit}>
                                Submit
                            </button>
                        </div>
                    </form>

                    <Form onSubmit={this.handleSubmit}>

                        <Row>

                            <Col>
                                <ThemeProvider
                                    theme={{
                                        reactDatepicker: {
                                            datepickerZIndex : 100000
                                        }
                                    }}
                                >
                                    <DateRangeInput
                                        onDatesChange={data => this.props.dateChangeFun(data)}
                                        onFocusChange={focusedInput => this.props.focusChangeFun(focusedInput)}
                                        startDate={this.props.startDate} // Date or null
                                        endDate={this.props.endDate} // Date or null
                                        focusedInput={this.props.focusedInput} // START_DATE, END_DATE or null
                                        unavailableDates={[new Date("2021-09-18")]}
                                    />

                                </ThemeProvider>
                            </Col>
                            <Col>
                                <Button type={"submit"}  variant="primary">
                                    {"+"} Book Room
                                </Button>
                            </Col>

                        </Row>

                        <Row>
                            <Col>
                                <Form.Control  value={this.state.firstname}
                                               onChange={this.handleInputChange}  type="text" size={"lg"} placeholder="First name" />
                            </Col>
                            <Col>
                                <Form.Control  value={this.state.lastname}
                                               onChange={this.handleInputChange} type="text" size={"lg"} placeholder="Last name" />
                            </Col>

                            <Col>
                                <Form.Control  value={this.state.email}
                                               onChange={this.handleInputChange} type="text"  size={"lg"} placeholder="Email" />
                            </Col>

                        </Row>
                    </Form>
                </Container>
            </div>

        );
    }
}


function mapStateToProps(state) {
    const {loading} = state.report;
    const {startDate} = state.daterange;
    const {endDate} = state.daterange;
    const {focusedInput} = state.daterange;
    const {reservations} = state.report;
    return {
        loading,
        reservations,
        startDate,
        endDate,
        focusedInput
    };
}

const connectedApp = connect(mapStateToProps, mapDispatchToProps)(App);
export {connectedApp as App};
