import React, {useReducer} from 'react';
import {connect} from 'react-redux';
import 'react-notifications/lib/notifications.css';
import {Button, Card, Container, Row,Col} from "react-bootstrap";
import {report} from "../_actions";
import {Modal, ModalBody, ModalFooter, ModalHeader} from "reactstrap";
import "../styles.css";
import {DateRangeInput} from "@datepicker-react/styled";


const style = {
    zIndex: "10000000",

};


function mapDispatchToProps(dispatch) {
    return {
        loadDashBoard: article => dispatch(report.loadDashboard(article))
    };
}




class App extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.openModal = this.openModal.bind(this);
        this.state = {
            showModal: false
        };

    }

    handleChange(event) {

    }

    componentDidMount() {
        this.props.loadDashBoard();
    }

    openModal(e) {
        e.preventDefault();
        this.setState({
            showModal: true
        });

    }

    closeModal() {
        this.setState({
            showModal: false
        });
    }

    render() {

        let cards = this.props.reservations.map((val, key) => {
            return (
                <React.Fragment>
                    <Card style={{ width: '18rem' }} className='col-md-2 m-2'>
                        <Card.Body>
                            <Card.Title>hi {val.reservation_date}</Card.Title>
                            <Card.Text>
                                {val.reservation_date}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </React.Fragment>
            )
        });


        return (
            <div className='App'>

                <Container>
                    <Row>
                        <Col>
                            <>
                                <Button onClick={this.openModal} variant="primary" size="lg" active>
                                    {"+"} Book Room
                                </Button>

                                <Modal  autoFocus={true} backdrop={false} zIndex={"100000000"}
                                       isOpen={this.state.showModal} fade={false}
                                        style={{maxWidth: '1600px', width: '80%'}}>

                                    <ModalHeader toggle={(e) => this.closeModal(e)}>
                                        <i className={'flaticon-add'}/>
                                        Available users
                                    </ModalHeader>

                                    <ModalBody>
                                        <Row>
                                            <Col>
                                                <DateRangeInput
                                                    onDatesChange={null}
                                                    onFocusChange={null}
                                                    startDate={null} // Date or null
                                                    endDate={null} // Date or null

                                                />
                                            </Col>
                                        </Row>

                                    </ModalBody>

                                </Modal>
                            </>
                        </Col>
                    </Row>

                    <Row>
                        {cards}
                    </Row>
                </Container>
            </div>

        );
    }
}


function mapStateToProps(state) {
    console.log(state);
    const {loading} = state.report;
    const {reservations} = state.report;
    return {
        loading,
        reservations
    };
}

const connectedApp = connect(mapStateToProps, mapDispatchToProps)(App);
export {connectedApp as App};
