import {conversationConstants, sendersConstants} from '../_constants';
import { conversationService } from '../_services';

export const conversation = {
    loadAllSenderConversations,
    messageSending,
    messageSent,
    showComposeModal,
    hideComposeModal,
    showDetails,
    hideDetails
};

const getPostsSuccess = users => ({
    type: conversationConstants.GET_RECEIVER_SUCCESS,
    payload: users,
});

const getPostsFailure = () => ({
    type: conversationConstants.GET_RECEIVER_FAILURE,
});


function loadAllSenderConversations(sender_id) {
    return dispatch => {
        conversationService.loadSenders(sender_id)
            .then(data => {
                dispatch(getPostsSuccess(data.payload.data));
            });
    };
}

function messageSending(){
    return { type: conversationConstants.GET_CONVERSATION_SENDING };
}
function messageSent(){
    return { type: conversationConstants.GET_CONVERSATION_SENT };
}

function showComposeModal(isReply,sendTo) {
    return{
        type: conversationConstants.SHOW_COMPOSE_MODAL,
        isReply: isReply,
        sendTo: sendTo
    };
}
function hideComposeModal() {
    return{
        type: conversationConstants.HIDE_COMPOSE_MODAL,
        isReply: false
    };
}
function showDetails(){
    return{
        type: conversationConstants.SHOW_DETAILS
    };
}
function hideDetails(){
    return{
        type: conversationConstants.HIDE_DETAILS
    };
}
