import React from 'react';
import {connect} from "react-redux";
import {userActions} from "../../_actions";
import axis from "axis.js";

class TeamMemberInfo extends React.Component {
    constructor(props){
        super(props);
        this.state = {showMenu : false};
    }
    handleDropDownMenu(e){
        e.preventDefault();
        const isShowing = this.state.showMenu;
        this.setState({
            showMenu: !isShowing
        });
    }


    componentDidMount() {
        this.props.dispatch(userActions.getUserProfile());
    }


    render() {
        let userPic = "https://via.placeholder.com/60";

        if(!axis.isUndefined(this.props.users.userProfile) && this.props.users.userProfile.pic){
            userPic = "//nihaoaupair.com/upload/team/profile/" + this.props.users.userProfile.id + "/" +this.props.users.userProfile.pic;
        }
        return(
            <div className="team-member-info">
                <div className="image">
                    <img src={userPic} alt="" />
                </div>
                <div className="info">
                    <h4 className="name">{this.props.users.userProfile.first_name + "" + this.props.users.userProfile.last_name}</h4>
                    <small>{this.props.users.userProfile.email}</small>
                </div>
            </div>
        );
    }
}
const mapStateToProps = state => state;
export default connect(mapStateToProps)(TeamMemberInfo);
