import {conversationConstants, userConstants} from '../_constants';

const initialState = {
    items: [],
    allUserPage: 1,
    meta: {},
    userProfile : {},
    picture_loading: false,
    loading:false,
    searchKeyword:""
};

export function users(state = initialState, action) {

    switch (action.type) {
        case userConstants.GETALL_REQUEST:
            return Object.assign({}, state, {
                loading: true
            });

        case userConstants.GETALL_SUCCESS:
            if(action.users.payload.html.meta.pagination.current_page === 1){
                return Object.assign({}, state, {
                    items: [...action.users.payload.html.data],
                    meta: {...action.users.payload.html.meta},
                    allUserPage: action.users.payload.html.meta.pagination.current_page,
                    loading: false,
                });
            }
            return Object.assign({}, state, {
                items: [...state.items,...action.users.payload.html.data],
                meta: {...action.users.payload.html.meta},
                allUserPage: action.users.payload.html.meta.pagination.current_page,
                loading: false,
            });

        case userConstants.GETALL_FAILURE:
            return Object.assign({}, state, {
                error: action.error
            });


        case userConstants.CONTACT_ADD_SUCCESS:
            return Object.assign({}, state, {
                add_items: action,
                add_contact_loading: true
            });


        case userConstants.CONTACT_ADD_REQUEST:
            return Object.assign({}, state, {
                add_contact_loading: true
            });

        case userConstants.UPDATE_REQUEST:
            return Object.assign({}, state, {
                update_contact_loading: true
            });

        case userConstants.UPDATE_SUCCESS:
            return Object.assign({}, state, {
                update_contact_loading: false,
                updateResponse: action.payload
            });

        case userConstants.UPDATE_FAILURE:
            return Object.assign({}, state, {
                update_contact_loading: false,
                updateResponse: action.payload
            });

        case userConstants.GET_PROFILE:
            return Object.assign({}, state, {
                userProfile: action.payload
            });

        case userConstants.PROFILE_PIC_REQUEST:
            return{
                ...state,
                picture_loading: true
            };
        case userConstants.PROFILE_PIC_SUCCESS:
            return{
                ...state,
                picture_loading: false
            };
        case userConstants.LOADING:
            return{
                ...state,
                loading: true
            };
        case userConstants.LOADING_DONE:
            return{
                ...state,
                loading: false
            };
        case userConstants.GET_SEARCH_SUCCESS:
            if(action.params.data.payload.html.meta.pagination.current_page === 1){
                return Object.assign({}, state, {
                    items: [...action.params.data.payload.html.data],
                    meta: {...action.params.data.payload.html.meta},
                    allUserPage: action.params.data.payload.html.meta.pagination.current_page,
                    loading: false,
                    searchKeyword : action.params.keyword
                });
            }
            return Object.assign({}, state, {
                items: [...state.items,...action.params.data.payload.html.data],
                meta: {...action.params.data.payload.html.meta},
                allUserPage: action.params.data.payload.html.meta.pagination.current_page,
                loading: false,
                searchKeyword : action.params.keyword
            });
        case userConstants.GET_AUPAIR_SUCCESS:
            return Object.assign({}, state, {
                items: [...action.payload.data],
                meta: {...action.payload.meta},
                allUserPage: action.payload.meta.pagination.current_page,
                loading: false,
            });
        case userConstants.GET_FAMILY_SUCCESS:
            return Object.assign({}, state, {
                items: [...action.payload.data],
                meta: {...action.payload.meta},
                loading: false,
            });
        default:
            return Object.assign({}, state, {});
    }
}
