import React from 'react';
import {connect} from 'react-redux';
import 'react-notifications/lib/notifications.css';
import {Button, Card, Container, Row,Col} from "react-bootstrap";
import {report} from "../_actions";
import {Modal, ModalBody, ModalFooter, ModalHeader} from "reactstrap";

const style = {
    zIndex: "10000000"
};


function mapDispatchToProps(dispatch) {
    return {
        loadDashBoard: article => dispatch(report.loadDashboard(article))
    };
}


class App extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {

    }

    componentDidMount() {
        this.props.loadDashBoard();
    }

    openModal(e) {
        e.preventDefault();
        this.setState({
            showModal: true
        });

    }

    render() {

        let cards = this.props.reservations.map((val, key) => {
            return (
                <React.Fragment>
                    <Card style={{ width: '18rem' }} className='col-md-2 m-2'>
                        <Card.Body>
                            <Card.Title>hi {val.reservation_date}</Card.Title>
                            <Card.Text>
                                {val.reservation_date}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </React.Fragment>
            )
        });


        return (
            <div className='App'>

                <Container>
                    <Row>
                        <Col>
                            <>
                                <Button onClick={this.handleChange} variant="primary" size="lg" active>
                                    {"+"} Book Room
                                </Button>

                                <Modal onOpened={true} autoFocus={true} backdrop={false} zIndex={"100000000"}
                                       isOpen={true} fade={false}
                                       style={style}>

                                    <ModalHeader toggle={(e) => this.closeModal(e)}>
                                        <i className={'flaticon-add'}/>
                                        Available users
                                    </ModalHeader>

                                    <ModalBody>
                                        <div className="min-height-container">
                                            Hello
                                        </div>

                                    </ModalBody>

                                </Modal>
                            </>
                        </Col>
                    </Row>

                    <Row>
                        {cards}
                    </Row>
                </Container>
            </div>

        );
    }
}


function mapStateToProps(state) {
    console.log(state);
    const {loading} = state.report;
    const {reservations} = state.report;
    return {
        loading,
        reservations
    };
}

const connectedApp = connect(mapStateToProps, mapDispatchToProps)(App);
export {connectedApp as App};
