import React from 'react';
import ConversationItemHeader from "./ConversationItemHeader";
import ConversationItemContent from "./ConversationItemContent";
import ConversationItemAttachment from "./ConversationItemAttachment";

class ConversationItemBody extends React.Component{
    constructor(props){
        super(props);
    }

    render() {
        return (
            <div className="conversation-item-body">
                <ConversationItemContent content={this.props.conversation.message}/>
                <ConversationItemAttachment attachments={this.props.conversation.attachment}/>
            </div>
        );
    }
}

export default ConversationItemBody;
