import {conversationConstants, userConstants} from '../_constants';
import { userService } from '../_services';
import axis from "axis.js";


export const userActions = {
    login,
    logout,
    getAll,
    updateUser,
    getUserProfile,
    profilePicLoading,
    profilePicLoadingDone,
    loading,
    loadingDone,
    getAupairs,
    getFamilies
};

function login(email, password) {
    return dispatch => {
        dispatch(request({ username: email }));

        userService.login(email, password)
            .then(
                user => {
                    if(!axis.isUndefined(user.status) && user.status === 'validation-error'){
                        dispatch(validationError(user));
                    } else if(!axis.isUndefined(user.status) && user.status === 'error'){
                        dispatch(failure(user));
                    } else{
                       dispatch(success(user));
                        history.push('/');
                    }
                },
                error => {
                    dispatch(failure(error));
                }
            );
    };

    function request(user) { return { type: userConstants.LOGIN_REQUEST, user }; }
    function success(user) { return { type: userConstants.LOGIN_SUCCESS, user }; }
    function validationError(error) { return { type: userConstants.LOGIN_VALIDATION_ERROR, error }; }
    function failure(error) { return { type: userConstants.LOGIN_FAILURE, error }; }
}

function logout() {
    return { type: userConstants.LOGOUT };
}

function getAll() {
    return dispatch => {
        dispatch(request());

        userService.getAll()
            .then(
                users => dispatch(success(users)),
                error => {
                    dispatch(failure(error));
                }
            );
    };

    function request() { return { type: userConstants.GETALL_REQUEST }; }
    function success(users) { return { type: userConstants.GETALL_SUCCESS, users }; }
    function failure(error) { return { type: userConstants.GETALL_FAILURE, error }; }
}

function updateUser(params) {
    return dispatch => {
        dispatch(request());

        userService.updateUser(params)
            .then(
                response => {
                    if (!axis.isUndefined(response)) {
                        const {status, payload} = response;
                        if (status === "validation-error") {
                            NotificationManager.error('Error', payload.c_password[0]);
                        } else if (status === 'success') {
                            NotificationManager.success('Success message', 'Password successfully updated');
                        }
                    }
                    dispatch(success(response));
                },
                error => {
                    dispatch(failure(error));
                }
            );
    };

    function request() { return { type: userConstants.UPDATE_REQUEST }; }
    function success(users) { return { type: userConstants.UPDATE_SUCCESS,   payload: users }; }
    function failure(error) { return { type: userConstants.UPDATE_FAILURE, payload : error }; }
}

function getUserProfile() {
    return dispatch => {
        userService.getUserProfile()
            .then(
                response => {
                    dispatch(success(response.payload));
                }
            );
    };
    function success(users) { return { type: userConstants.GET_PROFILE,   payload: users }; }
}
function getAupairs(page,query){
    return dispatch => {
        dispatch(loading());
        userService.getAupairs(page,query)
            .then(
                response => {
                    dispatch(success(response.payload));
                    dispatch(loadingDone());
                }
            );
    };
    function loading() { return { type: userConstants.LOADING}; }
    function loadingDone() { return { type: userConstants.LOADING_DONE}; }
    function success(data) { return { type: userConstants.GET_AUPAIR_SUCCESS,   payload: data }; }

}
function getFamilies(page,query){
    return dispatch => {
        dispatch(loading());
        userService.getFamilies(page,query)
            .then(
                response => {
                    dispatch(success(response.payload));
                    dispatch(loadingDone());
                }
            );
    };
    function success(data) { return { type: userConstants.GET_FAMILY_SUCCESS,   payload: data }; }
    function loading() { return { type: userConstants.LOADING}; }
    function loadingDone() { return { type: userConstants.LOADING_DONE}; }
}
function profilePicLoading(){
    return { type: userConstants.PROFILE_PIC_REQUEST };
}
function profilePicLoadingDone(){
    return { type: userConstants.PROFILE_PIC_SUCCESS };
}
function loading(){
    return { type: userConstants.LOADING };
}
function loadingDone(){
    return { type: userConstants.LOADING_DONE };
}
