import { reportConstants } from '../_constants';
import { reportService } from '../_services';

export const report = {
    loadDashboard,
    changeYear
};

const getReportSuccess = reports => ({
    type: reportConstants.REPORT_SUCCESS,
    payload: reports,
});

const getReportFailure = () => ({
    type: reportConstants.GET_RECEIVER_FAILURE,
});

const getReportLoading = (status) => ({
    type: reportConstants.LOADING,
    payload : status
});

const changeYearMode = (year) => ({
    type: reportConstants.CHANGE_YEAR,
    payload : year
});


function loadDashboard() {
    return dispatch => {
        dispatch(getReportLoading(true));
        reportService.loadDashboard()
            .then(data => {
                dispatch(getReportLoading(false));
                dispatch(getReportSuccess(data.payload));
            });
    };
}


function changeYear(year) {
    return dispatch => {
        dispatch(changeYearMode(year));
    };
}
