import {conversationConstants, sendersConstants} from '../_constants';


const initialState = {
    loading : false,
    receivers: [],
    data : [],
    message_sending: false,
    isReply: false,
    showComposeModal: false,
    showDetails: false,
    sendTo:null
};



export function conversation(state = initialState, action) {
    switch (action.type) {
        case conversationConstants.GET_RECEIVER_SUCCESS:
            return Object.assign({}, state, {
                receivers: action.payload,
                data : true
            });
        case conversationConstants.GET_CONVERSATION_SENDING:
            return{
                ...state,
                message_sending: true
            };
        case conversationConstants.GET_CONVERSATION_SENT:
            return{
                ...state,
                message_sending: false
            };
        case conversationConstants.SHOW_COMPOSE_MODAL:
            return{
                ...state,
                showComposeModal: true,
                isReply: action.isReply,
                sendTo: action.sendTo
            };
        case conversationConstants.HIDE_COMPOSE_MODAL:
            return{
                ...state,
                showComposeModal: false,
                isReply: false,
                sendTo:null
            };
        case conversationConstants.SHOW_DETAILS:
            return{
                ...state,
                showDetails: true
            };
        case conversationConstants.HIDE_DETAILS:
            return{
                ...state,
                showDetails: false
            };
        default:
            return state;
    }
}
